
public class Ch3OperatorEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		나머지 연산자
//		% 연산자
//		왼쪽의 값을 오른쪽 값으로 나우고 난 나머지 값의 결과를 반환하는 연산자
		
		int x = 10;
		int y = 8;
		
		int resultNum = 0;
		
		resultNum = x / y;
		System.out.println("몫 : " + resultNum);
		
		resultNum = x % y;
		System.out.println("나머지 : " + resultNum);
		
		
		
	}

}
